import React from 'react';
import GoogleMapReact from 'google-map-react';

class App extends React.Component{
  state = {
    showInfoWindow: false, //hide/show infowindow
    mapLoaded: false,
    center: {},
    stores: [
      {city: "Dease Lake", lat: 58.4374, lng: -129.9994},
      {city: "Fort Nelson", lat: 58.8050, lng: -122.6972},
      {city: "Terrace", lat: 54.5182, lng: -128.6032},
      {city: "Prince George", lat: 53.9171, lng: -122.7497},
      {city: "Whistler", lat: 50.1163, lng: -122.9574},
      {city: "Revelstoke", lat: 50.9981, lng: -118.1957},
      {city: "Creston", lat: 49.5, lng: -116.5135}
    ]
  }

  getCenter(){
    const stores = this.state.stores;
    var latitude = 0;
    var longitude = 0;
    for (let i = 0; i<stores.length; i++){
      latitude += stores[i].lat;
      longitude += stores[i].lng;
    }

    latitude = latitude / stores.length;
    longitude = longitude / stores.length;
    this.setState({
      center: {lat: latitude, lng: longitude}
    })
  } 

  render(){
    return (
      <div className="App" style={{height: '70vh', width: '60vw', textAlign: 'center'}}>
       <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyDAH-HZXwsL1DwvM7i0p3pPtrwrnzQhUxs'}}
        defaultCenter={{lat: 54.0562, lng: -124.7414}}
        defaultZoom={5.4} >

        {this.state.stores.map((store, index) => {
            return (
              <div key={index} lat={Math.ceil(store.lat)} lng={store.lng} 
              style={{display: 'inline-block'}}
              >
                <span style={{fontSize: '1.2em'}}>
                  14&deg;
                </span>
                  <img src="./icons/raining.png" alt="icon" style={{width: '3.4vw', height: '4.3vh'}}/> 
                  <span> {store.city} </span>
              </div>
            );
          })
        }
         <h4 lat={this.state.center.lat} lng={this.state.center.lng} >
          Center
        </h4>
        </GoogleMapReact>
      </div>
    );
  }

}

// export default GoogleApiWrapper({
//   apiKey: 'AIzaSyDAH-HZXwsL1DwvM7i0p3pPtrwrnzQhUxs'
// })(App);
export default App;
