import React from 'react'

function CityInfo(props){
	const store = props.store;
	return (
		<div style={{display: 'inline-block', textAlign: 'center', fontWeight: 'bold'}}
			onClick={(ev, store) => props.handleClick(ev, {store})}
		>
			<span className="cityName"> {store.name} </span>
			<img src={store.icon} alt="icon"  
			 	style={{width: '3.4vw', height: '4.3vh', display: 'inline-block'}} /> 
			<span style={{fontSize: '1.2em'}}>
            	{store.temp}&deg;
            </span>
		</div>
	);
}

export default CityInfo;
