import React from 'react';
import { 
  Map, 
  GoogleApiWrapper,
  InfoWindow,
  Marker
} from 'google-maps-react';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      showInfoWindow: false, //hide/show infowindow
      activeMarker: {}, //shows active marker upon click
      selectedPlace: {}, //show infowindow to selected place upon a marker
      mapLoaded: false,
      stores: [
        {city: "Dease Lake", lat: 58.4374, lng: -129.9994},
        {city: "Fort Nelson", lat: 58.8050, lng: -122.6972},
        {city: "Terrace", lat: 54.5182, lng: -128.6032},
        {city: "Prince George", lat: 53.9171, lng: -122.7497},
        {city: "Whistler", lat: 50.1163, lng: -122.9574},
        {city: "Revelstoke", lat: 50.9981, lng: -118.1957},
        {city: "Creston", lat: 49.0955, lng: -116.5135}
      ]
    }
  }

  onClose = (props) => {
    this.setState({
      showInfoWindow: false,
      activeMarker: null
    });
  }

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return <Marker key={index} position={{
        lat: store.lat,
        lng: store.lng
      }}
      onClick={() => console.log("You clicked me!")} />
    })
  }

  displayInfoWindows = () => {
    return this.state.stores.map((store, index) => {
      return (
        <InfoWindow 
          key={index}
          visible={this.state.mapLoaded}
          location={{
            lat: store.lat,
            lng: store.lng
          }}
        >
          <span>{store.city}</span>
        </InfoWindow>  
      );
    })
  }

  handleMapIdle = () => {
    this.setState({
      mapLoaded: true
    });
  }

  render(){

    return (
      <div className="App">
        <Map 
          google={this.props.google}
          zoom={5.2}
          style={{maxWidth: '70%', maxHeight: '70%'}}
          initialCenter={{
            lat: 54.6569,
            lng: -124.7417
          }}
        >

        </Map>
      </div>
    );
  }

}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyDAH-HZXwsL1DwvM7i0p3pPtrwrnzQhUxs'
})(App);
