import React from 'react';
import GoogleMap from 'google-map-react';
import CityInfo from './CityInfo';
import axios from 'axios';
import DetailedWeather from './DetailedWeather';

class App extends React.Component{
  state = {
    center: {},
    stores: [],
    showInfo: false,
    activeStore: {}
  }

  getCenter(){
    const stores = this.state.stores;
    var latitude = 0;
    var longitude = 0;
    for (let i = 0; i<stores.length; i++){
      latitude += stores[i].coords.lat;
      longitude += stores[i].coords.lng;
    }

    latitude = latitude / stores.length;
    longitude = longitude / stores.length;
    this.setState({
      center: {lat: latitude, lng: longitude}
    })
  } 

  componentDidMount(){
    axios.get("http://api.openweathermap.org/data/2.5/group", {
     params: {
      units: "metric",
      id: "6113365,5932311,6180144,5936286,5955902,6162949,6121621",
      appid: "d768b86ac2b5b13b09964365eb5f0128"
     }
    }).then(response => {
      var data = response.data.list;
      const cityWeathers = data.map(city => {
        return (
          {
            name: city.name,
            temp: city.main.temp,
            coords: {lat: city.coord.lat, lng: city.coord.lon},
            tempMin: city.main.temp_min,
            tempMax: city.main.temp_max,
            pressure: city.main.pressure,
            humidity: city.main.humidity,
            description: city.weather[0].description,
            windSpeed: city.wind.speed,
            icon: "http://openweathermap.org/img/wn/"+city.weather[0].icon+".png"
          }
        );
      });

      this.setState({
        stores: cityWeathers
      });

      this.getCenter();
    });
  }

  handleClick = (ev, store) => {
    const city = ev.target.parentElement.querySelector('.cityName').innerHTML;
    this.setState({
      currentStore: store,
      showInfo: true
    })
  }

  render(){
    return (
      <div className="App" style={{height: '70vh', width: '60vw', margin: '2px auto'}}>
       <GoogleMap
        bootstrapURLKeys={{ key: 'AIzaSyDAH-HZXwsL1DwvM7i0p3pPtrwrnzQhUxs'}}
        defaultCenter={{lat: 54.0562, lng: -124.7414}}
        defaultZoom={5.4} >

          {this.state.stores.map((store, index) => {
              return (
                <CityInfo 
                  key={index} 
                  lat={Math.ceil(store.coords.lat)} 
                  lng={store.coords.lng} 
                  store={store}
                  handleClick={this.handleClick}
                />
              );
            })
          }

          {this.state.showInfo && <DetailedWeather store={this.state.currentStore}/>}

        </GoogleMap>
      </div>
    );
  }
}

export default App;
